import React, { useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";

function App() {
  const [files, setFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState("");
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  // Handle file selection
  const handleFileChange = (e) => {
    setFiles(e.target.files);
  };

  // Upload selected files to the backend
  const uploadFiles = async () => {
    if (!files || files.length === 0) return;

    const formData = new FormData();
    for (let i = 0; i < files.length; i++) {
      formData.append("files", files[i]);
    }

    try {
      const res = await axios.post("http://localhost:8000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setUploadStatus(`Uploaded ${res.data.files.length} file(s).`);
    } catch (err) {
      setUploadStatus("Upload failed.");
    }
  };

  // Send a message (question) to the backend
  const sendMessage = async () => {
    if (!input.trim()) return;

    // Add user message to chat
    const newMessages = [...messages, { role: "user", text: input }];
    setMessages(newMessages);
    setInput("");

    try {
      const formData = new FormData();
      formData.append("question", input);
      const res = await axios.post("http://localhost:8000/ask", formData);
      const answer = res.data.answer;

      // Add assistant answer to chat
      setMessages([...newMessages, { role: "assistant", text: answer }]);
    } catch (err) {
      setMessages([
        ...newMessages,
        { role: "assistant", text: "Error retrieving answer." },
      ]);
    }
  };

  return (
    <div style={{ backgroundColor: "#fff", minHeight: "100vh", padding: 20 }}>
      <motion.h1
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        style={{ textAlign: "center", marginBottom: 20 }}
      >
        RAG Chat
      </motion.h1>

      {/* Upload Section */}
      <div style={{ textAlign: "center", marginBottom: 20 }}>
        <input type="file" multiple onChange={handleFileChange} />
        <button onClick={uploadFiles}>Upload Files</button>
        <p>{uploadStatus}</p>
      </div>

      {/* Chat Window */}
      <div
        style={{
          border: "1px solid #ccc",
          borderRadius: 4,
          width: "80%",
          maxWidth: 600,
          margin: "0 auto",
          padding: 10,
          height: "60vh",
          overflowY: "auto",
          marginBottom: 20
        }}
      >
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              textAlign: msg.role === "user" ? "right" : "left",
              margin: "10px 0",
            }}
          >
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              style={{
                display: "inline-block",
                padding: "8px 12px",
                borderRadius: 8,
                backgroundColor: msg.role === "user" ? "#f0f0f0" : "#e0ffe0",
                maxWidth: "70%",
                wordWrap: "break-word"
              }}
            >
              {msg.text}
            </motion.div>
          </div>
        ))}
      </div>

      {/* Input Section */}
      <div style={{ textAlign: "center" }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask a question..."
          style={{
            width: "70%",
            maxWidth: 500,
            padding: 8,
            marginRight: 10,
          }}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
